package com.metiz.pelconnect;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.fragment.app.FragmentTransaction;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;public class HomeScreenActivity extends BaseActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_home_screen);    }    public void replaceFragment(Fragment fr) {        FragmentManager fm = getSupportFragmentManager();        FragmentTransaction fragmentTransaction = fm.beginTransaction();        fragmentTransaction.replace(R.id.container_home, fr);        fragmentTransaction.commit();    }}